    this.timeout(packageLoaderTestTimeout);
    // There is the problem with '--no-save' flag for 'npm install' command for npm v6.
    // Installing npm dependencies with the `--no-save` flag will remove
    // other dependencies that were installed in the same manner (https://github.com/npm/cli/issues/1460).
    // So we should workaround it passing all packages for install to only one npm install command
    const getMkdrip = packageLoader_1.PackageLoader.getInstance().generateGetPackageFunction(mkdirpPackageConfig, rimrafPackageFirst);
    const getRimraf = packageLoader_1.PackageLoader.getInstance().generateGetPackageFunction(rimrafPackageFirst, mkdirpPackageConfig);
    const [mkdirpPackage, rimrafPackage] = yield Promise.all([getMkdrip(), getRimraf()]);
    assert.strictEqual(isNotEmptyPackage(rimrafPackage) && isNotEmptyPackage(mkdirpPackage), true, "Not all packages has been installed and required");
    const installedVersionOfMkdirp = yield getPackageVersionsFromNodeModules(sampleProjectPath, mkdirpPackageConfig.getPackageName());
    const installedVersionOfRimraf = yield getPackageVersionsFromNodeModules(sampleProjectPath, rimrafPackageFirst.getPackageName());
    assert.strictEqual(installedVersionOfMkdirp, mkdirpPackageConfig.getVersion(), `Wrong installed version of ${mkdirpPackageConfig.getPackageName()} package`);
    assert.strictEqual(installedVersionOfRimraf, rimrafPackageFirst.getVersion(), `Wrong installed version of ${rimrafPackageFirst.getPackageName()} package`);
});

